import React from "react";
import useForm from "./useForm";
import validate from "./validateInfo";
import "./Form.css";

function FormSignup({ submitForm }) {
  const { handleChange, values, handleSubmit, errors } = useForm(
    submitForm,
    validate
  );

  function callApi() {
    fetch("http://127.0.0.1:8000/api/enroll", {
      method: "POST",
      headers: {
        accept: "application/json",
        body: JSON.stringify(values),
      },
    });
  }

  function callApiPay() {
    fetch("http://127.0.0.1:8000/api/pay", {
      method: "POST",
      headers: {
        accept: "application/json",
        body: JSON.stringify(values),
      },
    });
  }

  function callApiBatch() {
    fetch("http://127.0.0.1:8000/api/batch", {
      method: "POST",
      headers: {
        accept: "application/json",
        body: JSON.stringify(values),
      },
    });
  }

  return (
    <div className="form-content-right">
      <form className="form" onSubmit={handleSubmit}>
        <h1>Get started us with today !!!! Join today for the Yoga Classes</h1>
        {/* user name */}
        <div className="form-inputs">
          <label htmlFor="name" className="form-label">
            Name
          </label>
          <input
            id="name"
            type="text"
            name="username"
            className="form-input"
            placeholder="Enter your name"
            value={values.username}
            onChange={handleChange}
          />
          {errors.username && <p>{errors.username}</p>}
        </div>
        {/*  */}

        {/* user email*/}
        <div className="form-inputs">
          <label htmlFor="email" className="form-label">
            Email
          </label>
          <input
            id="email"
            type="email"
            name="email"
            className="form-input"
            placeholder="Enter your email"
            value={values.email}
            onChange={handleChange}
          />
          {errors.email && <p>{errors.email}</p>}
        </div>
        {/*  */}
        {/* user age */}

        <div className="form-inputs">
          <label htmlFor="age" className="form-label">
            Age
          </label>
          <input
            id="age"
            type="number"
            name="age"
            className="form-input"
            placeholder="Enter your Age"
            value={values.age}
            onChange={handleChange}
          />
          {errors.age && <p>{errors.age}</p>}
        </div>
        <div className="form-inputs">
          <label for="batch">Batch Timings</label>
          <select
            id="batch"
            name="batch"
            value={values.batch}
            onChange={handleChange}
          >
            <option value="6-7 AM">6-7 AM</option>
            <option value="7-8 AM">7-8 AM</option>
            <option value="8-9 AM">8-9 AM</option>
            <option value="5-6 PM">5-6 PM</option>
          </select>
        </div>
        <div className="btn">
          <div>
            <button
              className="form-input-btn1"
              type="submit"
              name="enroll"
              onClick={callApi}
            >
              Enroll
            </button>
          </div>

          <div>
            <button
              className="form-input-btn2"
              type="submit"
              name="edit"
              onClick={callApiPay}
            >
              Edit Batch
            </button>
          </div>

          <div>
            <button
              className="form-input-btn3"
              type="submit"
              name="payment"
              onClick={callApiBatch}
            >
              Make Payment
            </button>
          </div>
        </div>
      </form>
    </div>
  );
}

export default FormSignup;
